import React from 'react';

export default function CallModal({
  open,
  callType = 'video',
  user = {},
  isIncoming = false,
  isVideo = true,
  onAccept,
  onReject,
  onEnd,
  localStream,
  remoteStream,
  status = '',
}) {
  if (!open) return null;
  const avatar = user && user.avatar;
  const username = user && user.username;
  // Determine if we are in an active call (show End Call only)
  const inCall = status && (status.toLowerCase().includes('connecting') || status.toLowerCase().includes('accepted') || status.toLowerCase().includes('in-call'));
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-60">
      <div className="bg-white rounded-xl shadow-xl p-6 w-full max-w-xs flex flex-col items-center relative">
        {/* Avatar and name */}
        {avatar ? (
          <img src={avatar} alt="avatar" className="w-16 h-16 rounded-full object-cover border mb-2" />
        ) : (
          <div className="w-16 h-16 rounded-full bg-gray-300 flex items-center justify-center text-2xl font-bold text-white mb-2">{username ? username[0] : '?'}</div>
        )}
        <div className="font-semibold text-lg mb-1 truncate w-full text-center">{username || 'Unknown'}</div>
        <div className="text-xs text-gray-500 mb-4">{callType === 'video' ? 'Video Call' : 'Voice Call'}</div>
        {/* Video streams */}
        {isVideo && (
          <div className="w-full flex flex-col items-center mb-4 relative" style={{ minHeight: 220 }}>
            {/* Remote video large */}
            <div className="w-full flex justify-center items-center">
              <video
                autoPlay
                playsInline
                ref={el => { if (el && remoteStream) el.srcObject = remoteStream; }}
                className="w-56 h-40 bg-black rounded-xl shadow-lg object-cover"
                style={{ display: remoteStream ? 'block' : 'none' }}
              />
            </div>
            {/* Local video as small overlay */}
            {localStream && (
              <div className="absolute bottom-2 right-2 w-20 h-16 bg-black rounded-lg shadow-md border-2 border-white overflow-hidden" style={{ zIndex: 2 }}>
                <video
                  autoPlay
                  playsInline
                  muted
                  ref={el => { if (el && localStream) el.srcObject = localStream; }}
                  className="w-full h-full object-cover"
                />
              </div>
            )}
            {/* If no remote stream, show local video large (pre-connection) */}
            {!remoteStream && localStream && (
              <video
                autoPlay
                playsInline
                muted
                ref={el => { if (el && localStream) el.srcObject = localStream; }}
                className="w-56 h-40 bg-black rounded-xl shadow-lg object-cover"
              />
            )}
          </div>
        )}
        {/* Status */}
        {status && <div className="text-xs text-gray-600 mb-2">{status}</div>}
        {/* Buttons */}
        <div className="flex gap-4 mt-2">
          {isIncoming && !inCall ? (
            <>
              <button className="bg-green-500 text-white px-4 py-2 rounded-lg font-semibold" onClick={onAccept}>Accept</button>
              <button className="bg-red-500 text-white px-4 py-2 rounded-lg font-semibold" onClick={onReject}>Reject</button>
            </>
          ) : (
            <button className="bg-red-500 text-white px-4 py-2 rounded-lg font-semibold" onClick={onEnd}>End Call</button>
          )}
        </div>
      </div>
    </div>
  );
}
