import React from 'react';

export default function CallModal({
  open,
  callType = 'video',
  user = {},
  isIncoming = false,
  isVideo = true,
  onAccept,
  onReject,
  onEnd,
  localStream,
  remoteStream,
  status = '',
}) {
  if (!open) return null;
  const avatar = user && user.avatar;
  const username = user && user.username;
  const showAcceptReject = isIncoming && !/accepted|connecting|in-call/i.test(status);
  const showEndCall = !showAcceptReject;
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-80">
      <div className="bg-white rounded-2xl shadow-2xl w-[90vw] h-[90vh] max-w-[700px] max-h-[700px] flex flex-col items-center relative overflow-hidden p-0">
        {/* Avatar and name (overlay, top left) */}
        <div className="absolute top-4 left-4 flex items-center gap-3 z-10 bg-white/80 rounded-lg px-4 py-2 shadow">
          {avatar ? (
            <img src={avatar} alt="avatar" className="w-10 h-10 rounded-full object-cover border" />
          ) : (
            <div className="w-10 h-10 rounded-full bg-gray-300 flex items-center justify-center text-xl font-bold text-white">{username ? username[0] : '?'}</div>
          )}
          <div className="flex flex-col min-w-0">
            <span className="font-semibold text-gray-900 text-base truncate">{username || 'Unknown'}</span>
            <span className="text-xs text-gray-500">{callType === 'video' ? 'Video Call' : 'Voice Call'}</span>
          </div>
        </div>
        {/* Video streams */}
        {isVideo && (
          <div className="w-full h-full flex-1 flex items-center justify-center relative">
            {/* Remote video large */}
            <video
              autoPlay
              playsInline
              ref={el => { if (el && remoteStream) el.srcObject = remoteStream; }}
              className="w-full h-full object-cover bg-black"
              style={{ display: remoteStream ? 'block' : 'none', borderRadius: 'inherit' }}
            />
            {/* Local video overlay */}
            {localStream && (
              <video
                autoPlay
                playsInline
                muted
                ref={el => { if (el && localStream) el.srcObject = localStream; }}
                className="absolute bottom-6 right-6 w-32 h-32 bg-black rounded-lg border object-cover shadow-lg z-20"
                style={{ display: localStream ? 'block' : 'none' }}
              />
            )}
          </div>
        )}
        {/* Status and buttons (bottom center) */}
        <div className="absolute bottom-6 left-1/2 -translate-x-1/2 flex flex-col items-center w-full px-4 z-10">
          {status && <div className="text-xs text-gray-700 mb-3 text-center bg-white/80 rounded px-3 py-1 shadow">{status}</div>}
          <div className="flex gap-4 mt-2 justify-center">
            {showAcceptReject ? (
              <>
                <button className="bg-green-500 text-white px-6 py-3 rounded-xl font-semibold text-lg shadow" onClick={onAccept}>Accept</button>
                <button className="bg-red-500 text-white px-6 py-3 rounded-xl font-semibold text-lg shadow" onClick={onReject}>Reject</button>
              </>
            ) : showEndCall ? (
              <button className="bg-red-500 text-white px-6 py-3 rounded-xl font-semibold text-lg shadow" onClick={onEnd}>End Call</button>
            ) : null}
          </div>
        </div>
      </div>
    </div>
  );
}
