import React from 'react';

export default function CallModal({
  open,
  callType = 'video',
  user = {},
  isIncoming = false,
  isVideo = true,
  onAccept,
  onReject,
  onEnd,
  localStream,
  remoteStream,
  status = '',
}) {
  if (!open) return null;
  const avatar = user && user.avatar;
  const username = user && user.username;
  // Show Accept/Reject only if incoming and status is exactly 'Connecting...'
  const showAcceptReject = isIncoming && status.trim() === 'Connecting...';
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-90">
      <div className="bg-black shadow-xl w-screen h-screen flex flex-col items-center justify-center relative" style={{ borderRadius: 0, padding: 0 }}>
        {/* Video streams fullscreen */}
        {isVideo && (
          <div className="w-full h-full flex items-center justify-center relative">
            {/* Remote video fullscreen */}
            <video
              autoPlay
              playsInline
              ref={el => { if (el && remoteStream) el.srcObject = remoteStream; }}
              className="w-full h-full object-cover bg-black"
              style={{ display: remoteStream ? 'block' : 'none' }}
            />
            {/* Local video small overlay */}
            <video
              autoPlay
              playsInline
              muted
              ref={el => { if (el && localStream) el.srcObject = localStream; }}
              className="absolute bottom-6 right-6 w-32 h-32 bg-black rounded-lg border object-cover shadow-lg"
              style={{ display: localStream ? 'block' : 'none' }}
            />
          </div>
        )}
        {/* Avatar and name (overlay) */}
        <div className="absolute top-8 left-8 flex items-center gap-4 bg-black bg-opacity-60 px-4 py-2 rounded-lg">
          {avatar ? (
            <img src={avatar} alt="avatar" className="w-12 h-12 rounded-full object-cover border" />
          ) : (
            <div className="w-12 h-12 rounded-full bg-gray-300 flex items-center justify-center text-xl font-bold text-white">{username ? username[0] : '?'}</div>
          )}
          <div>
            <div className="font-semibold text-lg text-white">{username || 'Unknown'}</div>
            <div className="text-xs text-gray-200">{callType === 'video' ? 'Video Call' : 'Voice Call'}</div>
          </div>
        </div>
        {/* Status (overlay) */}
        {status && <div className="absolute top-8 right-8 text-xs text-white bg-black bg-opacity-60 px-4 py-2 rounded-lg">{status}</div>}
        {/* Buttons (overlay, bottom center) */}
        <div className="absolute bottom-8 left-1/2 -translate-x-1/2 flex gap-4">
          {showAcceptReject ? (
            <>
              <button className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold text-lg" onClick={onAccept}>Accept</button>
              <button className="bg-red-500 text-white px-6 py-3 rounded-lg font-semibold text-lg" onClick={onReject}>Reject</button>
            </>
          ) : (
            <button className="bg-red-500 text-white px-6 py-3 rounded-lg font-semibold text-lg" onClick={onEnd}>End Call</button>
          )}
        </div>
      </div>
    </div>
  );
}
