import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Login from './pages/Login';
import Register from './pages/Register';
import RoomList from './components/RoomList';
import UserList from './components/UserList';
import MessageList from './components/MessageList';
import { io } from 'socket.io-client';

function App() {
  const [authPage, setAuthPage] = useState('login');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [loadingRooms, setLoadingRooms] = useState(false);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [messages, setMessages] = useState([]);
  const [loadingMessages, setLoadingMessages] = useState(false);
  const [messageInput, setMessageInput] = useState("");
  const [sending, setSending] = useState(false);
  const socketRef = useRef(null);

  // Persist login
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const savedToken = localStorage.getItem('token');
    if (savedUser && savedToken) {
      setUser(JSON.parse(savedUser));
      setToken(savedToken);
    }
  }, []);

  // Connect/disconnect socket on login/logout
  useEffect(() => {
    if (!token) return;
    socketRef.current = io('http://localhost:5001', {
      auth: { token },
      autoConnect: true,
    });
    return () => {
      socketRef.current?.disconnect();
    };
  }, [token]);

  // Join/leave room on selection
  useEffect(() => {
    if (!socketRef.current || !selectedRoom) return;
    socketRef.current.emit('joinRoom', selectedRoom);
    return () => {
      if (selectedRoom) socketRef.current.emit('leaveRoom', selectedRoom);
    };
  }, [selectedRoom]);

  // Listen for new messages
  useEffect(() => {
    if (!socketRef.current) return;
    const handler = (msg) => {
      setMessages((prev) => [...prev, msg]);
    };
    socketRef.current.on('newMessage', handler);
    return () => {
      socketRef.current.off('newMessage', handler);
    };
  }, []);

  // Fetch rooms and users after login
  useEffect(() => {
    if (!token) return;
    setLoadingRooms(true);
    setLoadingUsers(true);
    axios.get('/api/rooms', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        setRooms(res.data);
        if (res.data.length > 0) setSelectedRoom(res.data[0]._id);
      })
      .finally(() => setLoadingRooms(false));
    axios.get('/api/users', { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setUsers(res.data))
      .finally(() => setLoadingUsers(false));
  }, [token]);

  // Fetch messages when selectedRoom changes
  useEffect(() => {
    if (!token || !selectedRoom) return;
    setLoadingMessages(true);
    axios.get(`/api/messages/${selectedRoom}`, { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setMessages(res.data))
      .finally(() => setLoadingMessages(false));
  }, [token, selectedRoom]);

  const handleLogin = (user, token) => {
    setUser(user);
    setToken(token);
    localStorage.setItem('user', JSON.stringify(user));
    localStorage.setItem('token', token);
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('user');
    localStorage.removeItem('token');
  };

  const handleSelectRoom = (roomId) => {
    setSelectedRoom(roomId);
  };

  const handleDirectChat = async (userId) => {
    // Create or get direct room
    try {
      const res = await axios.post('/api/rooms/direct', { userId }, { headers: { Authorization: `Bearer ${token}` } });
      if (!rooms.find(r => r._id === res.data._id)) {
        setRooms(prev => [...prev, res.data]);
      }
      setSelectedRoom(res.data._id);
    } catch (err) {
      // handle error
    }
  };

  // Send message via socket and API
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!messageInput.trim() || !selectedRoom) return;
    setSending(true);
    try {
      // Emit to socket
      socketRef.current.emit('sendMessage', { roomId: selectedRoom, content: messageInput });
      // Also post to API for persistence (optional, can rely on backend to broadcast)
      await axios.post(
        `/api/messages/${selectedRoom}`,
        { content: messageInput },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessageInput("");
    } catch (err) {
      // Optionally handle error
    } finally {
      setSending(false);
    }
  };

  if (!user) {
    return authPage === 'login' ? (
      <Login onSwitch={() => setAuthPage('register')} onLogin={handleLogin} />
    ) : (
      <Register onSwitch={() => setAuthPage('login')} onRegister={handleLogin} />
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-200 flex items-center justify-center">
      <div className="w-full max-w-4xl bg-white rounded-xl shadow-lg p-8 flex flex-col md:flex-row gap-8">
        {/* Sidebar for user list and rooms */}
        <aside className="w-full md:w-1/3 border-r border-gray-200 pr-4">
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-purple-700 mb-2">Chat Rooms</h2>
            {loadingRooms ? <div className="text-gray-400">Loading...</div> :
              <RoomList rooms={rooms} selectedRoom={selectedRoom} onSelectRoom={handleSelectRoom} />}
          </div>
          <div>
            <h2 className="text-2xl font-bold text-blue-700 mb-2">Users</h2>
            {loadingUsers ? <div className="text-gray-400">Loading...</div> :
              <UserList users={users} onDirectChat={handleDirectChat} currentUserId={user.id || user._id} />}
          </div>
        </aside>
        {/* Main chat area */}
        <main className="flex-1 flex flex-col">
          <div className="mb-4 flex justify-between items-center">
            <h1 className="text-3xl font-extrabold text-gray-800">MERN Chat</h1>
            <button className="text-sm text-red-500 hover:underline" onClick={handleLogout}>Logout</button>
          </div>
          <div className="flex-1 bg-gray-50 rounded-lg p-4 mb-4 overflow-y-auto">
            {loadingMessages ? (
              <div className="text-gray-400 text-center">Loading messages...</div>
            ) : (
              <MessageList messages={messages} currentUserId={user.id || user._id} />
            )}
          </div>
          <form className="flex gap-2" onSubmit={handleSendMessage}>
            <input
              className="flex-1 rounded-lg border border-gray-300 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
              placeholder="Type your message..."
              value={messageInput}
              onChange={e => setMessageInput(e.target.value)}
              disabled={sending || !selectedRoom}
            />
            <button
              className="bg-purple-600 text-white px-6 py-2 rounded-lg font-semibold hover:bg-purple-700 transition"
              disabled={sending || !selectedRoom || !messageInput.trim()}
              type="submit"
            >
              {sending ? 'Sending...' : 'Send'}
            </button>
          </form>
        </main>
      </div>
    </div>
  );
}

export default App;
