import React, { useRef, useEffect } from 'react';

const MessageList = ({ messages, currentUserId }) => {
  const bottomRef = useRef(null);
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="space-y-3">
      {messages.map(msg => (
        <div key={msg._id} className={`flex ${msg.sender._id === currentUserId ? 'justify-end' : 'justify-start'}`}>
          <div className={`max-w-xs px-4 py-2 rounded-lg shadow text-sm ${msg.sender._id === currentUserId ? 'bg-purple-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
            <div className="font-semibold mb-1">{msg.sender.username}</div>
            <div>{msg.content}</div>
            <div className="text-xs text-gray-300 mt-1 text-right">{new Date(msg.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
          </div>
        </div>
      ))}
      <div ref={bottomRef} />
    </div>
  );
};

export default MessageList;
