import React from 'react';

export default function CallModal({
  open,
  callType = 'video',
  user = {},
  isIncoming = false,
  isVideo = true,
  onAccept,
  onReject,
  onEnd,
  localStream,
  remoteStream,
  status = '',
}) {
  if (!open) return null;
  const avatar = user && user.avatar;
  const username = user && user.username;
  const inCall = status && (status.toLowerCase().includes('connecting') || status.toLowerCase().includes('accepted') || status.toLowerCase().includes('in-call'));
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-90">
      <div className="w-full h-full flex flex-col items-center justify-center relative">
        {/* Top bar with avatar, name, and call type */}
        <div className="absolute top-0 left-0 w-full flex flex-col items-center pt-8 z-20">
          {avatar ? (
            <img src={avatar} alt="avatar" className="w-16 h-16 rounded-full object-cover border mb-2" />
          ) : (
            <div className="w-16 h-16 rounded-full bg-gray-300 flex items-center justify-center text-2xl font-bold text-white mb-2">{username ? username[0] : '?'}</div>
          )}
          <div className="font-semibold text-2xl mb-1 text-white drop-shadow text-center">{username || 'Unknown'}</div>
          <div className="text-sm text-gray-200 mb-2">{callType === 'video' ? 'Video Call' : 'Voice Call'}</div>
        </div>
        {/* Video streams */}
        {isVideo && (
          <div className="w-full h-full flex items-center justify-center relative">
            {/* Remote video fullscreen */}
            <video
              autoPlay
              playsInline
              ref={el => { if (el && remoteStream) el.srcObject = remoteStream; }}
              className="w-full h-full object-cover bg-black"
              style={{ display: remoteStream ? 'block' : 'none', borderRadius: 0 }}
            />
            {/* If no remote stream, show local video fullscreen (pre-connection) */}
            {!remoteStream && localStream && (
              <video
                autoPlay
                playsInline
                muted
                ref={el => { if (el && localStream) el.srcObject = localStream; }}
                className="w-full h-full object-cover bg-black"
                style={{ borderRadius: 0 }}
              />
            )}
            {/* Local video as small overlay */}
            {localStream && (
              <div className="absolute bottom-8 right-8 w-40 h-32 bg-black rounded-xl shadow-lg border-2 border-white overflow-hidden z-30">
                <video
                  autoPlay
                  playsInline
                  muted
                  ref={el => { if (el && localStream) el.srcObject = localStream; }}
                  className="w-full h-full object-cover"
                />
              </div>
            )}
          </div>
        )}
        {/* Status */}
        {status && <div className="absolute bottom-24 left-0 w-full text-center text-white text-sm z-20 drop-shadow">{status}</div>}
        {/* Buttons */}
        <div className="absolute bottom-8 left-0 w-full flex justify-center gap-6 z-20">
          {isIncoming && !inCall ? (
            <>
              <button className="bg-green-500 text-white px-8 py-3 rounded-full text-lg font-semibold shadow-lg" onClick={onAccept}>Accept</button>
              <button className="bg-red-500 text-white px-8 py-3 rounded-full text-lg font-semibold shadow-lg" onClick={onReject}>Reject</button>
            </>
          ) : (
            <button className="bg-red-500 text-white px-8 py-3 rounded-full text-lg font-semibold shadow-lg" onClick={onEnd}>End Call</button>
          )}
        </div>
      </div>
    </div>
  );
}
